import { Component, ViewChild  } from '@angular/core';
import { ModalController, IonicPage, Platform, LoadingController, NavController, NavParams, Content, ToastController } from 'ionic-angular';
import { MessageImageViewPage } from '../message-image-view/message-image-view';

import { AppDataServiceProvider } from '../../providers/app-data-service/app-data-service';
import { Storage } from '@ionic/storage';



@IonicPage()
@Component({
  selector: 'page-chat',
  templateUrl: 'chat.html',
})
export class ChatPage
{
	// @ViewChild(Content) content: Content;
    @ViewChild('myContent') content: Content;

  public text: any;
  public tempText: any;
	thread_id = null;
	user_id = null;
  friend_id = null;
  name = null;
	image = null;
	message = null;
  public canvasWidth;
  public canvasHight;
	RefreshInterval;
  refreshIntervalTime = 10000; //10sec
  options = 
  {
    maximumImagesCount: 1,
    width: 800,
    height: 600,
    quality: 85,
    outputType: 1
  }
  spinner;
  emojisToolId = false;
  emojis = [
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        'data:image/png;base64,',
        ];

  constructor(
  	public navCtrl: NavController, 
  	public navParams: NavParams, 
    private toast: ToastController,
    public storage : Storage,
    private DataService: AppDataServiceProvider,
    private modal: ModalController,
    public loading: LoadingController,
    public platform: Platform,
  	)
  {
    // console.log(moment().format('YYYY-MM-DD HH:mm:ss'));
    
  	this.user_id   = this.navParams.get('user_id');
    this.friend_id = this.navParams.get('friend_id');
    this.name = this.navParams.get('name');
  	this.image = this.navParams.get('image');
    this.thread_id = 'message_' + this.user_id+'_'+ this.friend_id;

    //Request from Server and save the respose;
  	if (this.user_id && this.friend_id)
  	{
      this.storage.get(this.thread_id)
      .then( (response)=>{ this.text = response; });

      this.DataService.GetMessages(this.user_id,this.friend_id)
  		.then(responce => 
  		{
        this.tempText = responce;
        for (var i = 0; i < this.tempText.length ; i++)
        {
          if (!this.text) { this.text = [];}
          this.text.push(responce[i]);
          this.storage.set(this.thread_id, this.text); 
        }
  		});
  	}
  	else
    {
     this.toast.create
      ({
          message:  'user not logged in' ,
          duration: 5000,
          position: 'top'
      }).present();
    }
  }

  ionViewDidEnter(){setTimeout(()=>{this.content.scrollToBottom();},500); }
  ionViewDidLeave() { clearInterval(this.RefreshInterval); }
  ionViewDidLoad()
  {
    this.canvasWidth = this.platform.width();
    this.canvasHight = this.platform.height();
    this.storage.get(this.thread_id)
    .then( (response)=>{ this.text = response; });

	  var that = this;
  	this.RefreshInterval = setInterval(function() 
    {
  		if (that.user_id && that.friend_id)
  		{
  	    that.DataService.GetMessages(that.user_id,that.friend_id)
  			.then(responce => 
  			{ 
          if (responce) 
          {
            that.tempText = responce;
            for (var i = 0; i < that.tempText.length ; i++)
            {
              if (!that.text) { that.text = [];}
              that.text.push(responce[i]);
              that.storage.set(that.thread_id, that.text); 
            }
          }
  	    		// that.content.scrollToBottom();
  			});
  		}

  	}, this.refreshIntervalTime);
  }

  sendMessage(type  = 'text')
  {

    let tempMessage = this.message;
    this.message = '';

    if (tempMessage)
    {
    	if (this.user_id && this.friend_id)
    	{
        let newMessage =  
        {
          user_id    : this.user_id,
          friend_id  : this.friend_id,
          message    : tempMessage,
          type       : type,
          direction  : 'sent',
          status     : 'seen',
          sentStatus : true,
        };
        if (!this.text) { this.text = []; }
        this.text.push(newMessage);
        this.storage.set(this.thread_id, this.text);
	    	this.DataService.SendMessage(this.user_id,this.friend_id, tempMessage, type)
  			.then(responce => 
  			{
          if (responce == 'error')
          {
            for (var i = 0; i < this.text.length ; i++)
            {
              if (this.text[i].sentStatus == false)
              {
                this.text.pop(this.text[i]);
                this.storage.set(this.thread_id, this.text); 
              }
            }
            this.toast.create({message:  'failed to sent message, please retry' ,duration: 5000,position: 'top'}).present();
          }
          else
          {
            this.toast.create({message:  'message sent' ,duration: 5000,position: 'top'}).present();
            for (var i = 0; i < this.text.length ; i++)
            {
              if (this.text[i].sentStatus == true)
              {
                this.text[i].sentStatus = false;
                this.storage.set(this.thread_id, this.text); 
              }
            }
          }
        });
	    	this.content.scrollToBottom();
    	}
    	else
	    {
	       this.toast.create
	        ({
	              message:  'ID is not set' ,
	              duration: 5000,
                position: 'top'
	        }).present();
	    }

    }else
    {
       this.toast.create
        ({
              message:  'Please Enter Message' ,
              duration: 5000,
              position: 'top'
        }).present();
    }
  }
  sendSticker(emoji)
  {
    this.emojisToolId = false;
    let type = 'sticker'; //sticker
    this.message = emoji;
    this.sendMessage(type);
  }

  sendImage($event) : void 
  {
  this.readThis($event.target);
  console.log('asdasd');
  }

readThis(inputValue: any): void {
  var file:File = inputValue.files[0];
  var myReader:FileReader = new FileReader();
  var that = this;
  let type = 'image';

  myReader.onloadend = (e) =>
  {
    that.message = myReader.result;
    that.sendMessage(type);

  }
  myReader.readAsDataURL(file);
}
  emojisToolToggle() { (this.emojisToolId) ?  this.emojisToolId = false  : this.emojisToolId = true;}


  imageViewer(image)
  {
    const Model = this.modal.create(MessageImageViewPage, {image: image});
    Model.present();
  }
  showLoading(){this.spinner =  this.loading.create({content: "Please wait..."});this.spinner.present();}
  hideLoading(){ this.spinner.dismiss(); }
}